void init_SdaiTransformation( Registry& reg ) {
    std::string str;
        config_control_design::t_transformation = new SelectTypeDescriptor (
                  ~(sdaiINSTANCE),        //unique elements,
                  "Transformation",        // Name
                  sdaiSELECT,        // FundamentalType
                  config_control_design::schema,        // Originating Schema
                  "SELECT (Item_Defined_Transformation, Functionally_Defined_Transformation)",        // Description
                  (SelectCreator) create_SdaiTransformation);        // Creator function
        config_control_design::schema->AddType(config_control_design::t_transformation);
    config_control_design::t_transformation -> Elements ().AddNode (config_control_design::e_item_defined_transformation);
    config_control_design::t_transformation -> Elements ().AddNode (config_control_design::e_functionally_defined_transformation);
    reg.AddType (*config_control_design::t_transformation);
}

